test.config:
indexer
{
	mem_limit			= 16M
}

searchd
{

}

source src
{
	type			= mysql

	sql_query = SELECT document_id, text, mva, tag, jsn FROM test_table

	sql_attr_multi = uint mva from field
	sql_attr_multi = bigint mva from field

	sql_attr_uint	= tag
    sql_attr_json   = jsn
}

index idx
{
	source			= src
	path			= /idx
	min_word_len	= 1
}

index dist
{
	type					= distributed
	agent					= :idx
	agent_connect_timeout	= 1000
	agent_query_timeout		= 3000
}

queries.sphinxql:

select mva from idx where id=5;
select mva from dist where id=5;


select to_string(mva) a from idx where id=5;
select to_string(mva) a from dist where id=5;


select jsn from idx where id=3;
select jsn from dist where id=3;


select * from idx;
select * from dist;
