{
	mem_limit		= 16M
}

searchd
{
	
}

source srcmain
{
	type			= mysql
	

	sql_query		= SELECT * FROM test_table WHERE document_id in (1,2,3,4,5,6,7,8)
	sql_attr_uint	= tag1
	sql_attr_uint	= tag2
	sql_attr_multi	= uint mva1 from field
	sql_attr_multi	= uint mva2 from field
}

