test.config:
indexer
{
	mem_limit = 16M
}

searchd
{

}

source test
{
	type = mysql


	sql_query = SELECT id, n, text FROM test_table;
	sql_attr_uint = n
}

index test
{
	source	= test
	path	= /test
}

source src_csv1
{
	type = csvpipe
	csvpipe_command = cat /data1.csv
	csvpipe_field = title
}

index csv1
{
	source = src_csv1
	path = /csv1
}

source src_xml1
{
	type = xmlpipe2
	xmlpipe_command = cat /data1.xml
}

index xml1
{
	source = src_xml1
	path = /xml1
}

index rt
{
	type			= rt
	rt_field		= body
	path			= /rt
}

index rt2
{
	type			= rt
	rt_field		= body
	path			= /rt2
}

test.db_create:
CREATE TABLE test_table
(
	id BIGINT NOT NULL,
	n INT NOT NULL DEFAULT 42,
	text VARCHAR(255) NOT NULL
);

