test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{

}

source srctest1
{
	type			= mysql


	sql_query			= SELECT document_id, group_id, group_id2, title, content FROM test_table
	sql_attr_uint		= group_id
	sql_attr_uint		= group_id2
}

