{
	sql_query			= SELECT 10+document_id, group_id, group_id2, title, content FROM test_table
}

index test1
{
	source			= srctest1
	path			= /test1
    charset_type 	= utf-8
}

index test2
{
	source			= srctest2
	path			= /test2
    charset_type 	= utf-8
}
