test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{
	
}

source src
{
	type			= mysql
	
	sql_query		= SELECT id, id AS tag, body FROM test_table
	sql_attr_uint	= tag
}

index idx
{
	source			= src
	path			= /main
    docinfo			= extern
}

test.custom_test:
$results = array ();

$idlist = "";
for ( $i=10000; $i<20000; $i++ )
	$idlist .= "$i,";

$client->SetSelect ( "*, IN(@id,1," . $idlist . "3) AS q, crc32('the') as crc" );
$client->SetFilter ( "q", array(1) );

$r = $client->Query ( "" );
if ( $r )
{
	unset ( $r["time"] );
	$results[] = $r;
} else
	$results[] = $client->GetLastError();
