test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{

	workers = threads
}

source test
{
	type			= mysql

	sql_query		= SELECT * FROM test_table
}

index test
{
	source	= test
	path	= /test

	blend_chars = @, |, +, ., *, !, (, ), [, ], {, }
}

source test1
{
	type			= mysql

	sql_query		= SELECT 1 as document_id, 'zzzzzz buzzzz' as text
}

index test1
{
	source	= test1
	path	= /test1

	blend_chars = ., @,
	min_word_len = 2
}

source test2
{
	type			= mysql

	sql_query		= SELECT 1 as document_id, 'dummy a11-22 text' as text UNION SELECT 2 as document_id, 'dummy 11-22 text' as text
}

index test2
{
	source	= test2
	path	= /test2

	blend_chars = -, @,
}

index star
{
	source	= test
	path	= /star

	blend_chars = @, ., !, (, ), [, ], {, }
	min_infix_len = 1
	dict = crc
}

index star_kw
{
	source	= test
	path	= /star_kw

	blend_chars = @, ., !, (, ), [, ], {, }
	min_infix_len = 2
	dict = keywords
}

source test3
{
	type			= mysql

	sql_query		= SELECT 1 as document_id, ' the"dog of my friend ' as text UNION SELECT 2 as document_id, ' dog"is my friend ' as text
}

index test3
{
	source	= test3
	path	= /test3

	blend_chars = -, @, "
}

index rt
{
	type	 = rt
	path	= /rt

	blend_chars = @

	rt_field	 = text
	rt_attr_uint	 = idd
}

source test4
{
	type			= mysql

	sql_query		= SELECT 1 as document_id, ' the dog of my friend ' as text UNION SELECT 2 as document_id, ' dog is my friend ' as text
}

index test4
{
	source	= test4
	path	= /test4
	blend_chars = U+2E
}

test.db_create:
CREATE TABLE test_table
(
	document_id INT NOT NULL,
	text VARCHAR(255) NOT NULL
);
