source src1
{
	type = xmlpipe2
	xmlpipe_command = cat /data1.xml
}

source src2
{
	type = xmlpipe2
	xmlpipe_command = cat /data2.xml
}

source src3
{
	type = xmlpipe2
	xmlpipe_command = cat /data3.xml
	xmlpipe_fixup_utf8 = 1
}

index idx1
{
	source = src1
	path = /idx1
	charset_type = utf-8
}

index idx2
{
	source = src2
	path = /idx2
	charset_type = utf-8
}

index idx3
{
	source = src3
	path = /idx3
	charset_type = utf-8
}

source src_embedded
{
	type = xmlpipe2
	xmlpipe_command = cat /data_embedded.xml

	xmlpipe_field = body
	xmlpipe_attr_uint	= idd
}

index embedded
{
	source = src_embedded
	path = /embedded
	charset_type = utf-8
}

source src_tsv1
{
	type = tsvpipe
	tsvpipe_command = cat /data1.tsv
	tsvpipe_field = title
	tsvpipe_field = content
	tsvpipe_attr_multi = tags
	tsvpipe_attr_uint = gid
}

index tsv1
{
	source = src_tsv1
	path = /tsv1
	charset_type = utf-8
