Test.Custom_test:
$client->SetMatchMode ( SPH_MATCH_EXTENDED2 );
$client->AddQuery ("(a b)|(c d)");
$client->AddQuery ("(a b)|(e f)");
$results = $client->RunQueries ();
for ( $i=0; $i<2; $i++ )
	if ( is_array($results) && is_array($results[$i]) )
		unset ( $results[$i]["time"] );
