test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{
	
}

source srcmain
{
	type			= mysql
	

	sql_query		= SELECT id, body, id*10 AS xid FROM test_table WHERE id in (1,2,3)
	sql_attr_uint	= xid
}

source srcdelta : srcmain
{
	sql_query		= SELECT id, body, id*100 AS xid FROM test_table WHERE id in (3,4,5)
}

index main
{
	source			= srcmain
	path			= /main
    charset_type 	= utf-8
}

index delta
{
	source			= srcdelta
	path			= /delta
    charset_type 	= utf-8
}
