test.custom_test:
global $g_locals;

$client->Open ();
$res1 = $client->Query ( "sonne", "test1" );
unset ( $res1["time"] );

$rv = 0;
$err = "";
exec ( $g_locals["indexer"]." --quiet --config ".testdir('config.conf')." --rotate --all", $err, $rv );

usleep ( 1500000 );
$res2 = $client->Query ( "sonne", "test1" );
unset ( $res2["time"] );
$client->Close();

$results = array ( $res1, $res2 );
