test.config:
indexer
{
	mem_limit			= 16M
}

searchd
{
	
	workers = threads
}

source src
{
	type			= mysql
	
	sql_query		= SELECT * FROM test_table WHERE document_id IN (1,2,3,4)
}

index main
{
	source			= src
	path			= /main
	charset_table	= 0..9, A..Z->a..z, _, a..z, (, ), $
}

source src_wf
{
	type			= mysql
	
	sql_query		= SELECT * FROM test_table WHERE document_id>4
}

index wf1
{
	source			= src_wf
	path			= /wf1
	morphology			= stem_en
	charset_table	= 0..9, A..Z->a..z, a..z,
	wordforms = /wordforms.txt
}

index wf2
{
	source			= src_wf
	path			= /wf2
	morphology			= stem_en
	charset_table	= 0..9, A..Z->a..z, a..z, -
	wordforms = /wordforms.txt
}

source src_wf3
{
	type			= mysql
	
	sql_query		= SELECT * FROM test_wf3
}


index wf3
{
	source			= src_wf3
	path			= /wf3
	morphology			= stem_en
	charset_table 	= -, 0..9, A..Z->a..z, _, a..z
	wordforms = /wf3.txt
}

index rt
{
	type = rt
	path			= /rt0
	rt_mem_limit = 32M
	rt_field = title
	rt_attr_uint=gid
	dict = keywords
	min_prefix_len = 1
	morphology = stem_en
	index_exact_words = 1
}

index exact_wf
{
	source			= src_wf3
	path			= /exact_wf
	dict = crc
	min_prefix_len = 1
	morphology = stem_en
	index_exact_words = 1
}

