test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{

compat_sphinxql_magics = 1
}

source src
{
	type			= mysql

	sql_query		= SELECT * FROM test_table
}

index idx
{
	source			= src
	path			= /main
    charset_type 	= utf-8
}

test.custom_insert:
for ( $i=0; $i<510; ++$i )
	mysql_query ( "INSERT INTO test_table VALUES ( 0, 'aaaa bbbb cccc dddd')" );
$line = "";
for ( $j=0; $j<520; ++$j )
	$line .= " x cccc dddd";
mysql_query ( "INSERT INTO test_table VALUES ( 0, 'aaaa bbbb x aaaa bbbb $line')" );
