test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{

}

source src
{
	type			= mysql

	sql_query		= SELECT * FROM test_table where id < 100
}

index idx
{
	source			= src
	path			= /main
	blend_chars	= U+002D # '-'
}

source src1
{
	type			= mysql

	sql_query		= SELECT id, title, 11 as idd FROM test_table where id in (101, 102)
	sql_attr_uint	= idd
}

index idx1
{
	source			= src1
	path			= /idx1
	wordforms		= /wf_multi.txt
}

source src2
{
	type			= mysql

	sql_query		= SELECT id, title, 11 as idd FROM test_table where id in (111, 112)
	sql_attr_uint	= idd
}

index idx2
{
	source			= src2
	path			= /idx2
	wordforms		= /wf_multi.txt
	morphology		= stem_en
	index_exact_words = 1
}

source src3
{
	type			= mysql

	sql_query		= SELECT id, title, body, 11 as idd FROM test_table1
	sql_attr_uint	= idd
}

index idx3
{
	source			= src3
	path			= /idx3
}

source src_hi1
{
	type			= mysql

	sql_query		= SELECT * FROM test_table where id >= 200
}

index hi1
{
	source			= src_hi1
	path			= /hi1
}

