    SELECT id, HIGHLIGHT({before_match='-+', after_match='+-', limit=0},TO_STRING('red fox big fox big red bear red fox'), 'bear NEAR/50 red') as h1  FROM hi1 WHERE id=200;

    SELECT id, HIGHLIGHT({before_match='-+', after_match='+-', limit=0},TO_STRING('red fox big fox big red bear red fox'), 'fox NEAR/50 bear') as h1  FROM hi1 WHERE id=200;
    SELECT id, HIGHLIGHT({before_match='-+', after_match='+-', limit=0},TO_STRING('red fox big fox big red bear red fox'), 'bear NEAR/50 fox') as h1  FROM hi1 WHERE id=200;

