test.db_create:
CREATE TABLE test_table
(
	id INTEGER AUTO_INCREMENT PRIMARY KEY NOT NULL,
	title VARCHAR(4096) NOT NULL
);

CREATE TABLE test_table1
(
	id INTEGER AUTO_INCREMENT PRIMARY KEY NOT NULL,
	title VARCHAR(255) NOT NULL,
	body VARCHAR(255) NOT NULL
);
