test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{
	
}

source src
{
	type			= mysql
	
	sql_query		= SELECT * FROM test_table
}

index idx
{
	source			= src
	path			= /main
    charset_type 	= utf-8
}

test.db_create:
CREATE TABLE test_table
(
	id INTEGER AUTO_INCREMENT PRIMARY KEY NOT NULL,
	title TEXT NOT NULL
);

test.custom_insert:
$line = "";
for ( $i=0; $i<10; ++$i )
{
	mysql_query ( "INSERT INTO test_table VALUES ( 0, 'a $line b')" );
	$line .="x ";
}

for ( $i=0; $i<510; ++$i )
	mysql_query ( "INSERT INTO test_table VALUES ( 0, 'e x f')" );
mysql_query ( "INSERT INTO test_table VALUES ( 0, 'e x f x x x e x f x x e x f x x')" );

$line = "";
for ( $i=0; $i<532; ++$i )
	$line .= " y y i x j";
mysql_query ( "INSERT INTO test_table VALUES ( 0, '$line')" );
