test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{
	
}

source src
{
	type			= mysql
	
	sql_query		= SELECT * FROM test_table
}

index idx
{
	source			= src
	path			= /main
}

source src1
{
	type			= mysql
	
	sql_query		= SELECT 1, 'M&M text in'
	sql_attr_uint	= idd
}

index idx1
{
	source			= src1
	path			= /idx1
	blend_chars		= &
}

test.custom_insert:
$line = "";
for ( $i=0; $i<10; ++$i )
{
	mysql_query ( "INSERT INTO test_table VALUES ( 0, 'a $line b')" );
	$line .="x ";
}

for ( $i=0; $i<510; ++$i )
	mysql_query ( "INSERT INTO test_table VALUES ( 0, 'e x f')" );
mysql_query ( "INSERT INTO test_table VALUES ( 0, 'e x f x x x e x f x x e x f x x')" );

$line = "";
for ( $i=0; $i<532; ++$i )
	$line .= " y y i x j";
mysql_query ( "INSERT INTO test_table VALUES ( 0, '$line')" );
