test.config:
indexer
{
	mem_limit			= 16M
}

searchd
{

	workers			= threads
	binlog_path		=
}

source srctest
{
	type			= mysql

	sql_query		= SELECT * FROM test_table WHERE id IN (1,2, 3, 4 )
	sql_attr_uint	= ival
	sql_attr_float	= fval
}

index test
{
	source			= srctest
	path			= /test
}

source src_mva
{
	type			= mysql

	sql_query		= SELECT id, 1 as idd, title as mva, 'test' FROM test_table WHERE id=10
	sql_attr_uint	= idd

	sql_attr_multi = uint mva from field
	sql_attr_multi = bigint mva from field

}

index mva
{
	source			= src_mva
	path			= /mva
}

source src_final
{
	type			= mysql

	sql_query		= SELECT id, ival as idd1, id*1000 as idd2, 'test' FROM test_table
	sql_attr_uint	= idd1
	sql_attr_uint	= idd2
}

index final
{
	source			= src_final
	path			= /final
	docinfo = extern
}
