{
	type			= mysql

	sql_query		= SELECT id, 1 as idd, title as mva, 'test' FROM test_table WHERE id=10
	sql_attr_uint	= idd
	sql_attr_multi	= uint mva from field
}

index mva
{
	source			= src_mva
	path			= /mva
}
