test.config:
indexer
{
	mem_limit = 16M
}

searchd
{

	workers = threads
	thread_stack = 256K
}

source test
{
	type = mysql

	sql_query = SELECT *, '1,2,3,4,5,6.0' AS poly2d_attr FROM test_table
	sql_attr_uint = aa
	sql_attr_multi = uint mm from field
	sql_attr_string = kk
	sql_attr_float = f1
	sql_attr_float = f2
	sql_attr_string = poly2d_attr
}

index test
{
	source	= test
	path	= /test
}

source cmp
{
	type = mysql

	sql_query = SELECT * FROM cmp_table
	sql_attr_multi = uint mva32 from field
	sql_attr_multi = bigint mva64 from field
	sql_attr_float = f1
	sql_attr_uint = gid
}
index cmp
{
	source	= cmp
	path	= /cmp
}

source cmp2
{
	type = mysql

	sql_query = SELECT * FROM cmp_table
	sql_attr_uint = f1
	sql_attr_uint = gid
}
index cmp2
{
	source	= cmp2
	path	= /cmp2
}

source strings
{
	type = mysql

	sql_query = SELECT *, 'dummy' FROM string_table
	sql_attr_string = string_value
}

index strings
{
	source	= strings
	path	= /strings
}

index dist
{
	type = distributed
	agent = :test
	agent_connect_timeout	= 1000
	agent_query_timeout		= 3000	
}

index strings_dist
{
	type = distributed
	agent = :strings
}

test.db_create:
CREATE TABLE string_table
(
	id INT NOT NULL,
	string_value VARCHAR(255) NOT NULL
);
