test.config:
indexer { mem_limit = 16M }
searchd {
workers = threads }
source srctest { type = mysql
sql_query = SELECT * FROM test_table }
index test { source = srctest path = /test charset_type = utf-8 morphology = stem_en index_exact_words = 1 expand_keywords = 1 }
index expanded_plain { source = srctest path = /expanded-plain charset_type = utf-8 docinfo = extern dict = keywords expand_keywords = 1 min_infix_len = 3 enable_star = 1 }
index expanded_rt { type = rt path = /expanded-rt charset_type = utf-8 dict = keywords expand_keywords = 1 min_infix_len = 3 enable_star = 1 rt_field = title rt_attr_uint = idd }
source src_empty { type = mysql
sql_query = SELECT * FROM empty_table sql_attr_uint = uid } index empty { type = plain path = /empty_kw source = src_empty min_infix_len = 3 dict = keywords }
source src_inline { type = mysql
sql_query = SELECT *, document_id+100 as idd FROM test_table sql_attr_uint = idd }
index plain_inline { source = src_inline path = /plain-inline charset_type = utf-8 docinfo = inline dict = keywords min_prefix_len = 1 enable_star = 1 }
