test.config:
indexer
{
	mem_limit = 16M
}

searchd
{

compat_sphinxql_magics = 1
}

source test
{
	type = mysql

	sql_query = SELECT * FROM test_table
	sql_attr_uint	= idd
	sql_attr_string = attr1
	sql_attr_string = attr2	
}

index test
{
	source	= test
	path	= /test
	docinfo	= extern
}

test.custom_insert:
$attr = '';
for ( $i=1; $i < 400; $i++ )
{
	$attr .= ' overrun'.$i;
}

$text = sprintf ( "insert into test_table
	(id, text, idd, attr1, attr2 ) values
	( 1, 'text', 1, '%s', '%s' )",
	$attr, $attr );
mysql_query ( $text );
