test.config:
indexer
{
	mem_limit = 16M
}

searchd
{
	
}

source test
{
	type					= mysql
	

	sql_query				= select id, text, idd from test;
	sql_joined_field		= text from query; select id, text from test_joined;
	sql_attr_uint			= idd
}

index test
{
	source	= test
	path	= /test

    charset_type			= utf-8
    docinfo					= extern
	
    phrase_boundary			= .
    phrase_boundary_step	= 2
	stopwords				= test_146/stopwords.txt
}

test.db_create:
create table test
(
	id int,
	text varchar(255),
	idd int
);

create table test_joined
(
	id int,
	text varchar(255)
);
