source auth
{
	type			= mysql

}

source src : auth
{
	sql_query = SELECT id, tag, gr, text FROM test_table WHERE id<4
	sql_query_pre = SET NAMES UTF8
	sql_attr_uint	= tag
	sql_attr_uint	= gr
}

source src1 : src
{
	sql_query = SELECT id, tag, gr, text FROM test_table WHERE id<3
}

source src2 : src
{
	sql_query = SELECT id, tag, gr, text FROM test_table WHERE id=3
}

source src51 : src
{
	sql_query = SELECT id, tag, gr, text FROM test_table WHERE id<6
}

source src52 : src
{
	sql_query = SELECT id, tag, gr, text FROM test_table WHERE id>5
}

source fake: auth
{
	sql_query = SELECT id, gr, text FROM test_table WHERE id<4
	sql_attr_uint	= gr
}

