test.config:
indexer { mem_limit = 16M }
searchd {
workers = threads }
source src { type = mysql
sql_query = SELECT id, idd1, body FROM test_table sql_attr_uint = idd1 }
index plain { source = src docinfo = extern charset_type = utf-8 path = /plain stopwords = /stopwords.txt blend_chars = . blend_mode = trim_both, trim_head }
index rt { type = rt docinfo = extern charset_type = utf-8 path = /rt stopwords = /stopwords.txt
rt_attr_uint = idd1 rt_field = body
rt_mem_limit = 8M }
index both { type = distributed local = plain local = rt }
