}

source test
{
	type			= mysql
	
	sql_range_step		= 2
	sql_query			= SELECT id, gid, title FROM test
	
	sql_joined_field	= text from ranged-query; select id, text from test where id between $start and $end; select 1, 5

	sql_attr_uint		= gid
	
	sql_attr_multi = uint mva from ranged-query; select id, mva from mva where id between $start and $end; select 2, 5
	sql_attr_multi = bigint mva from ranged-query; select id, mva from mva where id between $start and $end; select 2, 5
	
}

