test.config:
indexer { mem_limit = 16M }
searchd {
workers = threads }
source src { type = mysql
sql_query = SELECT id, idd1, str1, body FROM test_table sql_attr_uint = idd1 sql_attr_string = str1 }
index plain { source = src docinfo = extern charset_type = sbcs path = /plain }
index rt { type = rt docinfo = extern charset_type = sbcs path = /plain
rt_attr_uint = IDD1 rt_attr_uint = iDD2 rt_attr_string = Str1 rt_attr_string = Str2 rt_field = body
rt_mem_limit = 128K }
index dist { type = distributed local = plain local = rt }
