source src_main
{
	type			= mysql


	sql_query		= SELECT id, body, CONCAT(str1, ' main') AS str1, idd1, 100 as tag FROM test_table
	sql_attr_uint	= idd1
	sql_attr_uint	= tag
	sql_attr_string = str1
}

source src_delta1
{
	type			= mysql


	sql_query		= SELECT id, body, CONCAT(str1, ' delta1') AS str1, idd1, 101 as tag FROM test_table where id>3
	sql_attr_uint	= idd1
	sql_attr_uint	= tag
	sql_attr_string = str1
	sql_query_killlist = SELECT 2
}

source src_delta2
{
	type			= mysql


	sql_query		= SELECT id, body, 'delta2' as str1, CONCAT(str1, ' delta2') AS str2, idd1, 102 as tag FROM test_table where id>4
	sql_attr_uint	= idd1
	sql_attr_uint	= tag
	sql_attr_string = str1
	sql_attr_string = str2
	sql_query_killlist = SELECT 2
}


index main
{
	source			= src_main
	path			= /main
}


index delta2
{
	source			= src_delta2
	path			= /delta2
}

index dist1
{
	type = distributed
	local = main
	agent = :agent1_delta2
}

index dist2
{
	type = distributed
	local = delta2
	agent = :agent2_delta1
}

index dist3
{
	type = distributed
	agent = :agent1_main
	agent = :agent2_delta1
}

