test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{
	
}

source srctest
{
	type			= mysql
	

	sql_query		= SELECT *, 1 as idd FROM test_table
}

index test
{
	source			= srctest
	path			= /test
	exceptions		= /synonyms.txt
}

source srcstar
{
	type			= mysql
	

	sql_query		= SELECT *, 1 as idd FROM star_table
}

index star
{
	source			= srcstar
	path			= /star
	charset_table	= 0..9, A..Z->a..z, _, a..z, U+410..U+42F->U+430..U+44F, U+430..U+44F, *, %, ?
	exceptions		= /synonyms2.txt
	min_prefix_len = 1
}

index snip
{
	source			= srctest
	path			= /snip
	exceptions		= /synonyms3.txt
}

source src_wild1
{
	type			= mysql
	

	sql_query		= SELECT 1 as id, 11 as idd, '50% of 20% not 30* found' as text
	sql_attr_uint = idd
}

index wild1
{
	source				= src_wild1
	path					= /wild1
	charset_table	= a..z, _, A..Z->a..z
}

source src_e
{
	type			= mysql
	

	sql_query		= SELECT 1, 'life:) on mars' text
}

index e
{
	source			= src_e
	path			= /e
	exceptions		= test_165/synonyms4.txt
}
