test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{
	
}

source src
{
	type					= mysql
	
	sql_query		= SELECT 1, 'dummy', 1 as idd
	sql_attr_uint	= idd
}

index idx
{
	source				= src
	docinfo			= extern
	path					= /main
	blend_chars		= .
}

index idx2
{
	source				= src
	docinfo			= extern
	path					= /idx2
	morphology			= stem_en
	charset_table = 0..9, a..z, A..Z->a..z
	blend_chars		= U+0028, U+0029, U+002f, U+002b
	blend_mode = trim_none, trim_head, trim_tail, trim_both, skip_pure
}

