test.config:
indexer
{
	mem_limit			= 28M
}

searchd
{
	
	compat_results			= 0
	workers				= threads
}

source auth
{
	type			= mysql
	
}

source src : auth
{
	sql_query = SELECT id, tag, gr, text FROM test_table WHERE id<4
	sql_query_pre = SET NAMES UTF8
	sql_attr_uint	= tag
	sql_attr_uint	= gr
}

source src1 : src
{
	sql_query = SELECT id, tag, gr, text FROM test_table WHERE id<3
}

source src2 : src
{
	sql_query = SELECT id, tag, gr, text FROM test_table WHERE id=3
}

index dist_no
{
	source			= src
	path			= /idx
	docinfo			= extern
	charset_type	= utf-8
	min_word_len	= 1
}

index idx1 : dist_no
{
	source			= src1
	path			= /idx1
}

index idx2 : dist_no
{
	source			= src2
	path			= /idx2
}


index dist0
{
	type					= distributed
	local					= dist_no
	agent_connect_timeout	= 1000
	agent_query_timeout		= 3000
}

index dist1
{
	type					= distributed
	local					= idx1
	local					= idx2
	agent_connect_timeout	= 1000
	agent_query_timeout		= 3000
}

index dist2
{
	type					= distributed
	agent					= :idx1
	local					= idx2
	agent_connect_timeout	= 1000
	agent_query_timeout		= 3000
}

index dist3
{
	type					= distributed
	local					= idx1
	agent					= :idx2
	agent_connect_timeout	= 1000
	agent_query_timeout		= 3000
}
