index test1 { source = test1 path = /test1 }
source test2 { type = mysql
sql_query = select * from test_table2 sql_attr_uint = gid }
index test2 { source = test2 path = /test2 docinfo = extern html_strip = 1 index_sp = 1 }
source test3 { type = mysql
sql_query = select * from test_table3 sql_attr_uint = gid } index test3 { source = test3 path = /test3 docinfo = extern }
