rt_field = content morphology = none }
index optimized_filter { type = rt rt_mem_limit = 128k charset_type = utf-8 path = /optimized_filter
rt_field = content rt_attr_uint = idd0 rt_attr_uint = idd1 rt_attr_uint = idd2 rt_attr_uint = idd3 rt_attr_uint = idd4 rt_attr_uint = idd5 rt_attr_uint = idd6 rt_attr_uint = idd7 rt_attr_uint = idd8 rt_attr_uint = idd9 }
source src_hits { type = mysql
sql_query = SELECT id, title, 11 as idd FROM table_hits sql_attr_uint = idd }
