index hits_inline { source = src_hits path = /inline docinfo = extern charset_type = utf-8 }

test.db_create:
create table test_table1
(
	id int not null,
	gid int not null,
	title varchar(16384) not null
);

create table test_table2
(
	id int not null,
	gid int not null,
	title varchar(256) not null
);

create table test_table3
(
	id int not null,
	gid int not null,
	title varchar(64) not null
);

create table table_hits
(
	id int not null,
	title varchar(256) not null
);

