test.config:
indexer
{
	mem_limit			= 16M
}

searchd
{
	
	workers = threads
	binlog_path = #
}

source test
{
	type				= mysql
	
	sql_query			= select *, '1,2,3' mva32, '1,2,3' mva64 from test_table
	sql_attr_uint		= gid
	sql_field_string	= title
	sql_attr_multi		= uint mva32 from field mva32
	sql_attr_multi		= bigint mva64 from field mva64
}

index test
{
	source				= test
	path				= /test
	index_field_lengths = 1
}

index dist
{
	type = distributed
	agent = :test
	agent_connect_timeout	= 1000
	agent_query_timeout		= 3000	
}

source src_ext
{
	type = xmlpipe2
	xmlpipe_command = cat /data_ext.xml
	xmlpipe_field = title
	xmlpipe_field = content
	xmlpipe_attr_uint = gid
}

source src_int
{
	type = xmlpipe2
	xmlpipe_command = cat /data_int.xml
}

index xml_ext
{
	source				= src_ext
	path				= /xml_ext
	charset_type		= utf-8
	docinfo				= extern
	index_field_lengths = 1
}

index xml_int
{
	source				= src_int
	path				= /xml_int
	charset_type		= utf-8
	docinfo				= extern
	index_field_lengths = 1
}

