test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{

	workers = threads
	binlog_path = #
}

source src
{
	type			= mysql

	sql_query = SELECT * FROM test_table WHERE id<4
	sql_attr_uint	= section
	sql_attr_bigint = bint
	sql_attr_float = bfloat
}

source src1 : src
{
	sql_query = SELECT * FROM test_table WHERE id<3
}

source src2 : src
{
	sql_query = SELECT *  FROM test_table WHERE id=3
}

index dist_no
{
	source			= src
	path			= /idx
	docinfo			= extern
	charset_type	= utf-8
	min_word_len	= 1
}

index idx1 : dist_no
{
	source			= src1
	path			= /idx1
}

index idx2 : dist_no
{
	source			= src2
	path			= /idx2
}

index dist0
{
	type					= distributed
	local					= dist_no
	agent_connect_timeout	= 1000
	agent_query_timeout		= 3000
}

index dist1
{
	type					= distributed
	local					= idx1
	local					= idx2
	agent_connect_timeout	= 1000
	agent_query_timeout		= 3000
}

index dist2
{
	type					= distributed
	agent					= :idx1
	local					= idx2
	agent_connect_timeout	= 1000
	agent_query_timeout		= 3000
}

index dist3
{
	type					= distributed
	local					= idx1
	agent					= :idx2
	agent_connect_timeout	= 1000
	agent_query_timeout		= 3000
}

index dist4
{
	type					= distributed
	agent					= :idx1
	agent					= :idx2
	agent_connect_timeout	= 1000
	agent_query_timeout		= 3000
}

index dist5
{
	type					= distributed
	agent					= :dist_no
	agent_connect_timeout	= 1000
	agent_query_timeout		= 3000
}

index rt
{
	type = rt
	path			= /rt
	charset_type 	= utf-8
	docinfo			= extern

	rt_field			= body
	rt_attr_uint	= gid
	rt_attr_bigint	= bint
	rt_attr_float	= bfloat
}
