test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{
	
	workers			= threads
}

source disk
{
	type			= mysql
	
	sql_query		= select * from test_table
	sql_attr_uint	= gid
}

index disk
{
	source			= disk
	path			= /disk
	charset_table	= a..z, A..Z->a..z
}

index rt
{
	type			= rt
	path			= data/rt
	rt_attr_uint	= gid1
	rt_attr_uint	= gid2
	rt_field		= title
	rt_field		= content
	charset_table	= a..z, A..Z->a..z, _
}

test.db_create:
CREATE TABLE test_table
(
	id INTEGER PRIMARY KEY NOT NULL,
	gid INTEGER NOT NULL,
	title VARCHAR(255) NOT NULL
);
