test.config:
indexer { mem_limit = 16M }
searchd {
workers = threads }
source test { type = mysql
sql_query_pre = set names utf8 sql_query = select * from test_table sql_file_field = file_field }
index test { source = test path = /test
regexp_filter = (\pL\pL)-(\d+) => \1\2 regexp_filter = \b(\d+)\" => \1inch regexp_filter = (blue|red) => color }
source src_large { type = mysql
sql_query_pre = set names utf8 sql_query = select 1 id, 'test_194/file3.txt' file_field, 11 as idd sql_file_field = file_field sql_attr_uint = idd }
index test_large { source = src_large path = /test_large
regexp_filter = (blue|red) => color }
index rt1 { type = rt path = /rt1 docinfo = extern rt_mem_limit = 8M rt_attr_uint = idd rt_field = title dict = keywords regexp_filter = (blue|red) => color }

test.db_create:
CREATE TABLE test_table
(
	id INTEGER PRIMARY KEY NOT NULL,
	content VARCHAR(255) NOT NULL,
	file_field VARCHAR(255) NOT NULL
) CHARACTER SET utf8;
