test.config:
indexer
{
	mem_limit = 16M
}

searchd
{

	workers = threads
}

source src_base
{
	type = mysql

}

source src_1 : src_base
{
	sql_query = SELECT id, attr1, mva1, mva2, str1, str2, text FROM test_table WHERE idx = 'i1';

	sql_attr_multi = uint mva1 from field
	sql_attr_multi = uint mva2 from field
	sql_attr_string = str1
	sql_attr_string = str2
	sql_attr_uint	= attr1
}

source src_2 : src_1
{
	sql_query = SELECT id, attr1, mva1, mva2, str1, str2, text FROM test_table WHERE idx = 'i2';
}

index i1
{
	source			= src_1
	path			= /i1
	docinfo			= extern
	charset_type 	= utf-8

}

index i2
{
	source			= src_2
	path			= /i2
	docinfo			= extern
	charset_type 	= utf-8

}

index ll
{
	type			= distributed
	local = i1
	local = i2
}

index ld
{
	type			= distributed
	local			= i1
	agent			= :i2
	local = i2
}

index dd
{
	type			= distributed
	agent			= :i1
	agent			= :i2
}
