test.config:
indexer
{
	mem_limit = 16M
}

searchd
{

	workers = threads
	compat_sphinxql_magics = 0
}

source src_base
{
	type = mysql

}

source src_1 : src_base
{
	sql_query = SELECT * FROM test_table WHERE gid=1
	sql_attr_uint = gid
}

source src_2 : src_1
{
	sql_query = SELECT * FROM test_table WHERE gid=2
	sql_attr_uint = gid
}

index i1
{
	source			= src_1
	path			= /i1
	docinfo			= extern
	charset_type 	= utf-8

}

index i2
{
	source			= src_2
	path			= /i2
	docinfo			= extern
	charset_type 	= utf-8

}

index dd
{
	type			= distributed
	agent			= :i1
	agent			= :i2
}


source src_kw : src_1
{
	sql_query = SELECT 1 as idd, 11 as gid, 'test starts here' as text UNION SELECT 2 as idd, 11 as gid, 'the text from hox' as text UNION SELECT 3 as idd, 11 as gid, 'from moon to fox' as text UNION SELECT 4 as idd, 11 as gid, 'fox from the box' as text UNION SELECT 5 as idd, 11 as gid, 'home no here' as text
	sql_attr_uint = gid
}

index kw
{
	source			= src_kw
	path			= /kw
	docinfo			= extern
	charset_type 	= utf-8
	min_prefix_len = 1
	dict = keywords
}

test.db_create:
CREATE TABLE test_table
(
	id INT NOT NULL,
	gid INT NOT NULL,
	text VARCHAR(255) NOT NULL DEFAULT ''
);
