test.config:
common { lemmatizer_base = lemmatizer_base }
searchd {
workers = threads }
source test1 { type = mysql
sql_query_pre = set names utf8 sql_query = select * from test_table sql_attr_uint = gid }
index test1 { source = test1 path = /test1 charset_type = utf-8 morphology = lemmatize_ru_all blend_chars = @, . wordforms = /wordforms.txt }
index test2 : test1 { path = /test2 morphology = lemmatize_ru }
index test3 : test1 { path = /test3 index_exact_words = 1 morphology = stem_en,lemmatize_ru_all }
index test4 : test1 { path = /test4 index_exact_words = 1 morphology = lemmatize_en_all, stem_ru }
index testrt1 { type = rt path = /testrt1 charset_type = utf-8 morphology = lemmatize_ru_all blend_chars = @, . wordforms = /wordforms.txt rt_attr_uint = gid rt_field = title }
index testrt2 : testrt1 { path = /testrt2 morphology = lemmatize_ru }
source stalin_src { type = mysql
sql_query_pre = set names utf8 sql_query = select * from test_table where id=1000 sql_attr_uint = gid }
index stalin_plain { source = stalin_src path = /stalin_plain charset_type = utf-8 morphology = lemmatize_ru enable_star = 1 min_infix_len = 2 expand_keywords = 1 }
index stalin_rt { type = rt path = /stalin_rt charset_type = utf-8 morphology = lemmatize_ru_all dict = keywords enable_star = 1 min_infix_len = 2 expand_keywords = 1 rt_field = text rt_attr_uint = gid }

test.db_create:
CREATE TABLE test_table
(
	id INTEGER PRIMARY KEY NOT NULL,
	gid INTEGER NOT NULL,
	title VARCHAR(255) CHARACTER SET utf8 NOT NULL
);
