test.config:
indexer
{
	mem_limit = 16M
}

searchd
{

	workers = threads

	  seamless_rotate = 0
	  seamless_rotate = 1

}

source test
{
	type = mysql

	sql_query = select 1, 'dummy' dummy
}

index plain
{
	source	= test
	path	= /plain
}

source dummy_rot
{
	type = mysql

	sql_query = select 1, 'dummy' body
}

index dummy_rot
{
	source	= dummy_rot
	path	= /dummy_rot
}

test.custom_test:
$results[] = $client->Query ( 'dummy', 'plain' );
$results[] = $client->Query ( 'something', 'plain' );

global $this_test, $index_data_path;
$files = array ( 'spa', 'spd', 'sph', 'spi', 'spk', 'spm', 'spp', 'sps' );
foreach ( $files as $ext )
	copy ( "$this_test/refdata/plain.new.$ext", "$index_data_path/plain.new.$ext" );

RunIndexer ( $errors, '--rotate dummy_rot' ); // just to send SIGHUP to daemon	
sleep(2);

$results[] = $client->Query ( 'dummy', 'plain' );
$results[] = $client->Query ( 'something', 'plain' );

for ( $i=0; $i<count($results); $i++ )
	if ( is_array( $results[$i]) )
		unset ( $results[$i]["time"] );
