test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{

	workers = threads

		dist_threads = 0
		dist_threads = 4

}

source test1
{
	type			= mysql

	sql_query		= select *, 1 iid from test_table where (id%2)=1
	sql_attr_uint	= gid
	sql_attr_uint	= iid
}

source test2 : test1
{
	sql_query		= select *, 2 iid from test_table where (id%2)=0
}

index test1
{
	source			= test1
	path			= /test1
}

index test2
{
	source			= test2
	path			= /test2
}

index dist1
{
	type = distributed
	local = test1
	local = test2
}

index dist2
{
	type = distributed
	agent = :test1
	agent = :test2
}

test.db_create:
CREATE TABLE test_table
(
	id INTEGER PRIMARY KEY NOT NULL,
	gid INTEGER NOT NULL,
	title VARCHAR(255) NOT NULL
);
