test.config:
common
{
	lemmatizer_base	= lemmatizer_base
}

searchd
{

	workers				= threads
}

source test1
{
	type			= mysql

	sql_query_pre	= set names utf8
	sql_query		= select * from test_table
	sql_attr_uint	= gid
}

index test1
{
	source			= test1
	path			= /testen1
	morphology		= lemmatize_en_all
	blend_chars		= @, .
	wordforms		= /wordforms.txt
}

index test2 : test1
{
	path			= /testen2
	morphology		= lemmatize_en
}

index test2ruen : test2
{
	path			= /testruen2
	morphology		= lemmatize_en,lemmatize_ru
}

index test2red : test2
{
	path			= /testruende2
	charset_table = 0..9, A..Z, a..z, _, U+C0..U+D6, U+D9..U+DF, U+E0..U+F6, U+F9..U+FF, U+410..U+42F->U+430..U+44F, U+430..U+44F
	morphology		= lemmatize_en,lemmatize_ru,lemmatize_de	
}

index testrt1
{
	type			= rt
	path			= /testenrt1
	morphology		= lemmatize_en_all
	blend_chars		= @, .
	wordforms		= /wordforms.txt
	rt_attr_uint	= gid
	rt_field		= title
}

index testrt2 : testrt1
{
	path			= /testenrt2
	morphology		= lemmatize_en
}

index testrt2ruen : testrt2
{
	path			= /testruenrt2
	morphology		= lemmatize_en,lemmatize_ru
}

index testrt2red : testrt2
{
	path			= /testred2
	charset_table = 0..9, A..Z, a..z, _, U+C0..U+D6, U+D9..U+DF, U+E0..U+F6, U+F9..U+FF, U+410..U+42F->U+430..U+44F, U+430..U+44F
	morphology		= lemmatize_en,lemmatize_ru,lemmatize_de
}


index aot_plain
{
	source			= test1
	path			= /aot_plain
	docinfo			= extern
	morphology = lemmatize_ru_all
}

index aot_exact
{
	source			= test1
	path			= /aot_exact
	docinfo			= extern
	morphology = lemmatize_ru_all
	index_exact_words = 1
	min_prefix_len = 1
}

test.db_create:
CREATE TABLE test_table
(
	id INTEGER PRIMARY KEY NOT NULL,
	gid INTEGER NOT NULL,
	title VARCHAR(255) CHARACTER SET utf8 NOT NULL
);
