test.config:
indexer
{
	mem_limit = 16M
}

searchd
{

	compat_sphinxql_magics = 0
	dist_threads = 4

	# needed for "do not fetch too much" test below
	max_packet_size = 128K
	workers = threads
}

source base
{
	type = mysql

}

source t1 : base
{
	sql_query = SELECT 1 id, 'test one' title, 1 attr1, 234 attr2
	sql_attr_bool = attr1
	sql_attr_uint = attr2
}

source t2 : base
{
	sql_query = SELECT 2 id, 'test two' title, 123.0 attr1, 345 attr2
	sql_attr_float = attr1
	sql_attr_uint = attr2
}

source t3 : base
{
	# 4 rows, 10 cols, 4K/col = 160K total
	sql_query = SELECT id, gid, title t1, title t2, title t3, title t4 FROM t3
	sql_attr_uint = gid
	sql_field_string = t1
	sql_field_string = t2
	sql_field_string = t3
	sql_field_string = t4
}

index t1
{
	source = t1
	path = data/t1
}

index t2
{
	source = t2
	path = data/t2
}

index t3
{
	source = t3
	path = data/t3
}

index dt3
{
	type = distributed
	agent = :t3

test.db_create:
CREATE TABLE t3
(
	id INTEGER PRIMARY KEY NOT NULL,
	gid INTEGER NOT NULL,
	title VARCHAR(4096) NOT NULL
);
