test.config:
indexer
{
	mem_limit			= 28M
}

searchd
{

	workers					= threads
	listen 					= /tmp/searchd-test-241.sock
}

source src1
{
	type = mysql

	sql_query = SELECT 1 id, 11 idd, 'test me 1'

	sql_attr_uint = idd
}

source src2 : src1
{
	sql_query = SELECT 2 id, 11 idd, 'test me 2'
}

source src3 : src1
{
	sql_query = SELECT 3 id, 11 idd, 'test me 3'
}

index idx1
{
	source				= src1
	path				= /idx1
	docinfo 			= extern
}

index idx2
{
	source				= src2
	path				= /idx2
	docinfo 			= extern
}

index idx3
{
	source				= src3
	path				= /idx3
	docinfo 			= extern	
}

index dist
{
	type					= distributed
	local					= idx1
	agent					= :idx2
	agent 					= /tmp/searchd-test-241.sock:idx3
	agent_connect_timeout	= 1000
	agent_query_timeout		= 3000
}
