test.config:
searchd
{

	binlog_path =
	workers = threads
	seamless_rotate = 0
}

source sql1
{
	type = mysql


	sql_query = select id, text, mode from test_table where mode=1
	sql_attr_uint = mode
}

index plain1
{
	source = sql1
	path = /plain1
	docinfo = extern
}

source sql2
{
	type = mysql


	sql_query = select id, text, mode from test_table where mode=2
	sql_attr_uint = mode
}


index plain2
{
	source = sql2
	path = /plain2
	docinfo = extern
}

source sql3
{
	type = mysql


	sql_query = select id, text, mode from test_table
	sql_attr_uint = mode
}


index plain3
{
	source = sql3
	path = /plain3
	docinfo = extern
}


index dist_hung
{
	type = distributed
	agent = :plain1
	agent = :plain3
}

test.db_create:
create table test_table
(
	id int not null,
	text varchar(255) not null,
	mode int
);

test.custom_test:
$results = array();
$ql->Reconnect();

$results[] = 'all index presents';
$results[] = "\n" . $ql->Query ( "SHOW tables" );
$results[] = "\n" . $ql->Query ( "SELECT * FROM plain1, plain2" );

$orig_conf = file(testdir('config.conf'));

$new_conf = '';
$got_idx2 = false;
foreach ( $orig_conf as $line )
{
	if ( $got_idx2 || strstr ( $line, 'index plain2' ) )
	{
		if ( strstr ( $line, 'index plain2' ) )
			$got_idx2 = true;
		if ( $got_idx2 && strstr ( $line, '}' ) )
			$got_idx2 = false;
		$line = '#' . $line;
	}
	$new_conf .= $line;
}
file_put_contents(testdir('config.conf'), $new_conf);

RunIndexer ( $errors, '--rotate plain1' ); // just to send SIGHUP to daemon
sleep(2);

$results[] = 'plain2 disabled';
$results[] = "\n" . $ql->Query ( "SHOW tables" );

file_put_contents(testdir('config.conf'), $orig_conf);
RunIndexer ( $errors, '--rotate plain1' ); // just to send SIGHUP to daemon
sleep(2);

$results[] = 'plain2 re-enabled';
$results[] = "\n" . $ql->Query ( "SHOW tables" );
$results[] = "\n" . $ql->Query ( "SELECT * FROM plain1, plain2" );
