test.config:
indexer { mem_limit = 16M }
searchd {
}
source test { type = mysql
sql_query_pre = SET NAMES utf8 sql_query = SELECT id, body, idd FROM test_table sql_attr_uint = idd }
index plain1 { source = test path = /plain1 dict = keywords charset_table = a..z, _, A..Z->a..z, russian min_infix_len = 3 }
index rt1 { type = rt path = /rt1 rt_mem_limit = 128M rt_field = body rt_attr_uint = idd dict = keywords charset_table = a..z, _, A..Z->a..z, russian min_infix_len = 3 blend_chars = U+0023 }
source test2 { type = mysql
sql_query_pre = SET NAMES utf8 sql_query = SELECT id, body, idd FROM test_table where id in (1,4,6) sql_attr_uint = idd }
index plain2 { source = test2 path = /plain2 dict = keywords charset_table = a..z, _, A..Z->a..z min_infix_len = 3 morphology = stem_en index_exact_words = 1 }
index rt2 { type = rt path = /rt2 rt_mem_limit = 128M rt_field = body rt_attr_uint = idd dict = keywords charset_table = a..z, _, A..Z->a..z min_infix_len = 3 morphology = stem_en index_exact_words = 1 }
index rt3 { type = rt path = /rt3 rt_mem_limit = 128M rt_field = body rt_attr_uint = idd dict = keywords charset_table = a..z, _, A..Z->a..z, russian min_infix_len = 2 }
index dist1 { type = distributed local = rt1 agent = :plain1 }
index dist31 { type = distributed local = rt3 }
index dist32 { type = distributed agent = :rt3 }
index dist33 { type = distributed local = rt3 agent = :rt3 }
index rt_phrase { type = rt path = /rt_phrase rt_mem_limit = 128M rt_field = body rt_attr_uint = idd dict = keywords charset_table = a..z, _, A..Z->a..z min_infix_len = 2 }
index dist_phrase { type = distributed agent = :rt_phrase }

test.db_create:
CREATE TABLE test_table
(
	id INTEGER PRIMARY KEY NOT NULL,
	idd INTEGER NOT NULL,
	body VARCHAR(255) NOT NULL
) CHARACTER SET utf8;

