test.config:
searchd
{

	binlog_path =
	workers = threads
	seamless_rotate = 1
}

source sql1
{
	type = mysql


	sql_query = select id, text, mode from test_table
	sql_attr_uint = mode
}

index plain1
{
	source = sql1
	charset_table	= 0..9, A..Z->a..z, _, a..z
	docinfo = extern
	# replace_path1
}

index plain2
{
	source = sql1
	charset_table	= 0..9, A..Z->a..z, _, a..z
	docinfo = extern
	# replace_path2
}

index tmp1
{
	type			= template
	dict			= keywords
	charset_table	= 0..9, A..Z->a..z, _, a..z
}

test.db_create:
create table test_table
(
	id int not null,
	text varchar(255) not null,
	mode int
);

test.custom_test:
$results = array();
$ql->Reconnect();

$results[] = "\nbefore rotation";
$results[] = "\n" . $ql->Query ( "SHOW tables" );

$orig_conf = file(testdir('config.conf'));

global $index_data_path;
$new_conf = '';
foreach ( $orig_conf as $line )
{
	$placeholder1 = strpos ( $line, '# replace_path1' );
	if ( $placeholder1 )
		$line = "path = $index_data_path/plain1\n";
	$placeholder2 = strpos ( $line, '# replace_path2' );
	if ( $placeholder2 )
		$line = "path = $index_data_path/plain2 # replace_path2\n";
	$new_conf .= $line;
}
file_put_contents(testdir('config.conf'), $new_conf);

RunIndexer ( $errors, '--rotate plain1 plain2' );
sleep(2);

$orig_conf = file(testdir('config.conf'));

$new_conf = '';
foreach ( $orig_conf as $line )
{
	$placeholder2 = strpos ( $line, '# replace_path2' );
	if ( $placeholder2 )
		$line = "\n";
	$new_conf .= $line;
}
file_put_contents(testdir('config.conf'), $new_conf);

RunIndexer ( $errors, '--rotate plain1' );
sleep(2);

$results[] = "\nafter rotation";
$results[] = "\n" . $ql->Query ( "SHOW tables" );
$results[] = 'plain1';
$results[] = "\n" . $ql->Query ( "SELECT * FROM plain1 order by id asc" );
$results[] = 'tmp1';
$results[] = "\n" . $ql->Query ( " CALL SNIPPETS('Kph on Europe road 1110', 'tmp1', 'kph' ) " );
